/*LOGO CABEÃ‡ALHO*/
.header__logo {
    max-height: 70px;
    max-width: 300px;
    margin-bottom: 10px;
}

/*MENU PRINCIPAL*/
.header__nav {
    font-size: 13PX;
    line-height: 40PX;
    text-align: center;
    text-transform: uppercase;
}
.header__nav a {
  padding: 10px;
  color: #4c2705;
  font-weight: bold;
}
.header__nav a:hover {
  padding: 10px;
  color: #97721d;
  font-weight: bold;
  text-decoration: underline;
}
.header__category{
    ;
    padding-right: 10px;
}	
.header__category:hover {
    background: none;
}
.header__submenu {
    background: var(--light);
    border-top: 1px solid var(--gray);
    box-shadow: var(--box-shadow-sm);
    display: none;
    gap: var(--spacer-2);
    grid-template-columns: repeat(auto-fit,minmax(100%,1fr));
    margin-top: var(--spacer-4);
    max-width: fit-content;
    min-width: fit-content;
    overflow: auto;
    padding-left: 10px;
    padding-right: 10px;
    position: absolute;
    text-align: center;
}
/*CAMPO DE BUSCA*/
.header__search-input {
    display: block!important;
    border-color: #ededed;
    border: solid;
    border-width: thin;
}
.form-control {
color: #e5c6aa;
}
