css:
/*LOGO CABEÇALHO*/
.header__logo {
    max-height: 70px;
    max-width: 300px;
    margin-top: 10px;
    margin-bottom: 10px;
}

/*MENU PRINCIPAL*/
.header__nav {
    font-size: 13PX;
    line-height: 0PX;
    text-align: center;
    text-transform: uppercase;
    border-top: solid;
    border-color: #e7e7e7;
    border-width: 1px;
    padding-top: 10px;
}
.header__nav a {
    color: #4c2705;
    font-weight: bold;
    padding-top: 10px;
    padding-bottom: 10px;
    padding-left: 20px;
    padding-right: 20px;
}
.header__nav a:hover {
    color: #97721d;
    font-weight: bold;
    padding-top: 10px;
    padding-bottom: 10px;
    padding-left: 20px;
    padding-right: 20px;
    text-decoration: underline;
}

.header__category:hover {
        font-size: 13PX;
    line-height: 0PX;
    text-align: center;
    text-transform: uppercase;
    padding-top: 10px;
    padding-bottom: 10px;
    padding-left: 50px;
    padding-right: 30px
    font-weight: bold;
    text-decoration: underline;
  background: none;
}
.header__submenu {
    background: var(--light);
    border-top: 1px solid var(--gray);
    box-shadow: var(--box-shadow-sm);
    display: none;
    gap: var(--spacer-2);
    grid-template-columns: repeat(auto-fit,minmax(100%,1fr));
    margin-top: var(--spacer-4);
    max-width: fit-content;
    min-width: fit-content;
    overflow: auto;
    padding-left: 10px;
    padding-right: 10px;
    position: absolute;
    text-align: center;
}
/*CAMPO DE BUSCA*/
.header__search-input {
    display: block!important;
    border-color: #ededed;
    border: solid;
    border-width: thin;
}
.form-control {
color: #e5c6aa;
}

/*BANNER*/
.glide__bullet {
    height: 6px;
    margin-left: 1.4rem;
    width: 6px;
    background: #06060661;
    border: 0px solid #705133;
}

/*VITRINE - CARD PRODUTO*/
.product-card__name {
    font-weight: 600;
    text-align: center;
    font-size: 17px;
    padding-top: 10px;
    color: #4c2705;
}
.product-card__name:hover {
    font-weight: 600;
    text-align: center;
    font-size: 17px;
    padding-top: 10px;
    color: #97721D;
    text-decoration: underline;
}
.product-card__prices {
    text-align: center;
}
.product-card__prices .prices__discount {
    display: none;
    font-size: 13px;
    text-align: center;
}

/*PÁGINA DO PRODUTO*/

.breadcrumb {
    border: none;
}
.product_name{
    font-size: 1.6rem;
    font-weight: 600;
    margin-bottom: 0px;
    color: #4c2705;
}
.prices--big .prices__value {
    font-size: 2.0rem;
    color: #4c2705;
}
.variations__option {
    border: none;
    margin-bottom: var(--spacer-2);
    min-height: 2.3rem;
    min-width: 2.3rem;
    color: #97721d;
}
.variations__option: hover {
    border-color: #97721d;
    / *font-weight: 700; */
    text-decoration: underline;
}
.variations__option--selected:not(.disabled) {
    /* border-color: var(--secondary); */
    /* box-shadow: 0 0 0 0.1rem rgba(var(--secondary-rgb),.6); */
    font-weight: 700;
    font-size: 15px;
}
